import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  user = {
    name: "",
    status: "",
    avatar: "",
  }
  addUser(newUser: any){
    const newUserObj = {
      name: newUser.name,
      status: newUser.status,
      avatar: newUser.avatar,
    };
    this.user = newUserObj;
  }
