import { Component} from '@angular/core';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.scss'],
})
export class PostComponent {

}
<div class="body">
    <div class="body-user">
        <img src="" alt="Avatar">
        <div class="post-text">
            <h2>Name</h2>
            <p>I'm learning angular</p>
        </div>
    </div>
    <button type="button">Remove user</button>
</div>
